//Complexity: O(n^2) per sample.
#include<bits/stdc++.h>
using namespace std;
#include "../C-Quadratic-Sieve_C++/utils.h"
#include "../C-Quadratic-Sieve_C++/statistics.h"
#include "Int.h"

const int N=100005;
int _f[2][N],n1,n2;  //base=2^n1*3^n2.
inline void upd(int &x,int y){if (y<x)x=y;}
int calc(Int x,int n1,int n2){
	int *f=_f[0],*f1=_f[1]; Int v;
	memset(f,0x3f,sizeof(int)*(n1+1));
	f[0]=0;
	for (int i=0;i<=n2;++i){
		memset(f1,0x3f,sizeof(int)*(n1+1));
		v=x; v.init_div2();
		//f[j]: already divided by i 3's, and j 2's.
		for (int j=0;j<=n1;++j){
			upd(f[j+1],f[j]+v.mod2);
			upd(f1[j],f[j]+v.mod3);
			v.div2();
		}
		x/=3; swap(f,f1);
	}
	return f1[n1]+2*n1+3*n2;
}
double run_sample(int T=1){
	int t1=clock();
	double s=0; Int x;
	for (int i1=1;i1<=T;++i1){
		x.init_rand();
		s+=calc(x,n1,n2);
	}
	s/=T; s/=(n1*log(2)+n2*log(3))/log(3);
	printf("n1=%d n2=%d T=%d ave=%.6lf\n",n1,n2,T,s);
	printf("time=%d\n",clock()-t1);
	return s;
}
int heuristic_solve(Int x){
	int n=ceil(x.bit_length()/log2(6));
	return calc(x,n,n);
}
void run(){
	//n1=9; n2=8;
	//n1=11; n2=9;
	//n1=n2=10000;
	n1=11500; n2=10000;
	//n1=5600; n2=5000;
	Int::N=(n1*log(2)+n2*log(3))/log(2)/32+5;
	vector<double> a;
	for (int i1=1;;++i1){
		int T=10;
		double ans=run_sample(T);
		a.push_back(ans);
		printf("--------i1=%d #samples=%d %.6lf--------\n",i1,i1*T,mean(a));
	}
}
void test_heuristic(){
	//floor(pi*10^100)+10^1000
	const char x1[]="";
	//floor(sqrt(2)*10^100)+10^2000
	const char x2[]="";
	//floor(e*10^100)+10^3000
	const char x3[]="";
	Int n; Int::N=500;
	for (auto x:{x1,x2,x3}){
		n.read(x);
		int ans=heuristic_solve(n);
		printf("%d %.6lf\n",ans,ans/(n.bit_length()/log2(3)));
	}
}
int main(){
	srand(time(0));
	test_heuristic();
	run();
	return 0;
}

